
	//FUNCTIONS

	function parserPromociones(xmlstring){
			try{
					log('parserPromociones_init');
					var parser		 = 	new DOMParser();
					var xmlDocument  = 	parser.parseFromString( xmlstring, "text/xml" );
					var items 	 	 = 	xmlDocument.getElementsByTagName("item");
					var tableContent = "";
					var array_promociones= new Array();
					
					for (var i = 0; i < items.length; i++) {   
						var id			= items[i].getElementsByTagName("id");
						var titulo		= items[i].getElementsByTagName("titulo");
						var descripcion	= items[i].getElementsByTagName("decripcion");
						var precio		= items[i].getElementsByTagName("precio");
						var imagen		= items[i].getElementsByTagName("imagen");
						var fecha_vencimiento = items[i].getElementsByTagName("fecha_vencimiento");
						var updated		= items[i].getElementsByTagName("updated");
						
						if(id.length>0){
							if(id[0].firstChild != null){
									var item_promocion = new Array();
									if(id.length>0){					if(id[0].firstChild!=null){				item_promocion[0] = id[0].firstChild.nodeValue;			}else{ item_promocion[0] = "";fail=true;			}	}else{	item_promocion[0] = ""; fail=true; 	}
									if(titulo.length>0){				if(titulo[0].firstChild!=null){			item_promocion[1] = titulo[0].firstChild.nodeValue;		}else{ item_promocion[1]= "";	fail=true;			}	}else{	item_promocion[1] = ""; fail=true; 	}
									if(descripcion.length>0){			if(descripcion[0].firstChild!=null){	item_promocion[2] = descripcion[0].firstChild.nodeValue;	}else{ item_promocion[2]= ""; fail=true;			}	}else{	item_promocion[2] = ""; fail=true;	}
									if(precio.length>0){				if(precio[0].firstChild!=null){			item_promocion[3] = precio[0].firstChild.nodeValue;		}else{ item_promocion[3]= ""; fail=true;			}	}else{	item_promocion[3] = ""; fail=true;	}
									if(imagen.length>0){				if(imagen[0].firstChild!=null){			item_promocion[4] = imagen[0].firstChild.nodeValue;		}else{ item_promocion[4]= ""; fail=true;			}	}else{	item_promocion[4] = ""; fail=true;	}
									if(fecha_vencimiento.length>0){		if(fecha_vencimiento[0].firstChild!=null){item_promocion[5] = fecha_vencimiento[0].firstChild.nodeValue;}else{ item_promocion[5]= ""; fail=true;	}	}else{	item_promocion[5] = ""; fail=true;	}
									if(updated.length>0){				if(updated[0].firstChild!=null){		item_promocion[6] = updated[0].firstChild.nodeValue;		}else{ item_promocion[6]= ""; fail=true;			}	}else{	item_promocion[6] = ""; fail=true;	}
									array_promociones[array_promociones.length] = item_promocion;
							}
						}
					}
					if(items.length==0||xmlstring=="" ||xmlstring == null){
						
					}else{
						insertPromociones(array_promociones);
					}
				}catch(err){		log(err.message );		}
	}

	function insertPromociones(array_promociones){
		try{
				
				log('insertPromociones_init');
				if(mynamespace.db){
						mynamespace.db.transaction(
							function (t) {
								var i;
								var htmlcontent=document.getElementById('homecontenido');
								var len= array_promociones.length;
								for (var i = len; i--;){
									var item_obj =array_promociones[i];
									var delete_query	=	'DELETE FROM promociones WHERE id=' + item_obj[0] + ' ';
									var insert_query	=	'INSERT INTO promociones (id, titulo,descripcion,precio,imagen,fecha_vencimiento,updated) VALUES ('+item_obj[0]+', "'+item_obj[1]+'", "'+item_obj[2]+'","'+item_obj[3]+'","'+item_obj[4]+'","'+item_obj[5]+'","'+item_obj[6]+'")';
									t.executeSql(delete_query);
									t.executeSql(insert_query,
												[],
												function (tx, res) {	log("row Created Successfully");	},
												function (tx, err) {	log("ERROR - row creation failed - code: " + err.code + ", message: " + err.message);	}
												);	
								}
								displayPromociones();
							}
						);
				}
				
			}catch(err){		log(err.message );		}
	}
	
	function getXMLPromociones(){
		try{
				log('getXMLPromociones_init');
				if(blackberry.system.hasDataCoverage()){
					
						var ultima_fecha="1990-01-01";
						mynamespace.db = window.openDatabase('DB_latorre_v1', '', 'DBLaTorrev1', 20 * 1024 * 1024, errorOnDB);
						if(mynamespace.db){
								mynamespace.db.readTransaction(
									function (t) {
										t.executeSql("SELECT coalesce(MAX(updated),'2013-01-01') ultima_fecha FROM promociones", [], 
											function (tx, results) {
												
												for(var i=0;i<results.rows.length;i++){
													ultima_fecha = results.rows.item(i).ultima_fecha;
												}
												
											}
										);
									}
								);
						}
						promociones_url += "&fecha="+ultima_fecha;
						
						var xmlhttp = new XMLHttpRequest();
						xmlhttp.onreadystatechange=function(){
							if(xmlhttp.readyState==4 && xmlhttp.status==200){
									parserPromociones(xmlhttp.responseText);
							}else if(xmlhttp.readyState==4 && xmlhttp.status!=200){
									 //hideLoading();
							}
						}
						xmlhttp.open("GET",promociones_url,true);
						xmlhttp.send();
				}else{
						displayPromociones();						
				}
		}catch(err){		log(err.message );		}
	}
	
