	function displayPromociones(){
		log('displayPromociones_init');
		if(mynamespace.db){
					mynamespace.db.readTransaction(
						function (t) {
							t.executeSql('SELECT * FROM promociones ORDER BY updated ASC', [], 
										function (tx, results) {
												var i;
												var len = results.rows.length;
												var html_item='';
												for (i = 0; i < len; i++) {
														html_item+= '<li>'+
																	'	<img src="data:image/jpeg;base64,'+results.rows.item(i).imagen+'"/>'+
																	'	<div class="descripcion_promocion">'+
																	'		<div class="titulo_promocion">'+
																				results.rows.item(i).titulo+
																	'		</div>'+
																	'		<div class="descripcion">'+
																				results.rows.item(i).descripcion+
																	'		</div>'+
																	'		<div class="articulos_desde">Artículos desde<br/>'+
																	'			<span>Q '+results.rows.item(i).precio+' </span>'+
																	'		</div>'+
																	'	</div>'+
																	'</li>';
												}
												
												document.getElementById('listado_promociones').innerHTML=html_item;
										}
							);
						}
					);
			}
		
	}

	function init(){
		getXMLPromociones();
	}
	
